<?php$sess_annuaire = $this->session->userdata('annuaire');if(!empty($message)){	echo '<p class="message">'.$this->lang->line($message).'</p>';}// Filtresecho '<div class="filtres">';	echo form_open('annuaire/filtre', array('id' => 'form_contact_filtre', 'name' => 'form_contact_filtre'));		$this->table->add_row(			form_input(array('name' => 'input_nom_prenom', 'maxlength' => '255','size' => '60', 			'value' => ($sess_annuaire['filtre']['champ']=='nom' || $sess_annuaire['filtre']['champ']=='prenom' ? $sess_annuaire['filtre']['recherche'] : ''))),			form_radio(array('name' => 'radio_nom_prenom', 'value' => 'nom', 'checked' => ($sess_annuaire['filtre']['champ']=='nom'))).' '.$this->lang->line('annuaire_libelle_nom').' '			.form_radio(array('name' => 'radio_nom_prenom', 'value' => 'prenom', 'checked' => ($sess_annuaire['filtre']['champ']=='prenom'))).' '.$this->lang->line('annuaire_libelle_prenom').' ',			form_submit('submit_filtre_nom_prenom', $this->lang->line('general_valider'))			.'<button type="button" onclick="window.location.href=\''.site_url('/annuaire/reset_filtre').'\'" >'.$this->lang->line('general_effacer').'</button>'		);	echo $this->table->generate();	echo '</form>';	echo '<p>Nom : ';		for ($i=ord("A") ; $i<ord("Z") ; $i++){			echo '<a href="'.site_url('/annuaire/filtre/'.chr($i)).'" >'.chr($i).'</a>';		}	echo '</p>';echo '</div>';echo '<br /><br />';echo '<button type="button" onclick="window.location.href=\''.site_url('/annuaire/ajouter').'\'">'.$this->lang->line('general_ajouter').'</button>';echo '<br /><br />';if(!empty($liste_contact)){	echo '<table class="liste">';		echo '<thead>';			echo '<th><a href="'.site_url('/annuaire/tri/societe').'">'.$this->lang->line('annuaire_libelle_societe')			.($sess_annuaire['tri']['champ'] == 'societe' ? ' <span>'.$sess_annuaire['tri']['type'].'</span>' : '').'</a></th>';			echo '<th><a href="'.site_url('/annuaire/tri/nom').'">'.$this->lang->line('annuaire_libelle_nom')			.($sess_annuaire['tri']['champ'] == 'nom' ? ' <span>'.$sess_annuaire['tri']['type'].'</span>' : '').'</a></th>';			echo '<th><a href="'.site_url('/annuaire/tri/prenom').'">'.$this->lang->line('annuaire_libelle_prenom')			.($sess_annuaire['tri']['champ'] == 'prenom' ? ' <span>'.$sess_annuaire['tri']['type'].'</span>' : '').'</a></th>';			echo '<th><a href="'.site_url('/annuaire/tri/tel').'">'.$this->lang->line('annuaire_libelle_tel')			.($sess_annuaire['tri']['champ'] == 'tel' ? ' <span>'.$sess_annuaire['tri']['type'].'</span>' : '').'</a></th>';			echo '<th></th>';			echo '<th></th>';			echo '<th></th>';		echo '</thead>';		foreach($liste_contact as $contact){			echo '<tr>';							// Données				echo '<td>'.$contact['societe'].'</td>';				echo '<td>'.$contact['nom'].'</td>';				echo '<td>'.$contact['prenom'].'</td>';				echo '<td>'.$contact['tel'].'</td>';								// Actions				$statuts_dispos = $this->config->item('statut');				if($statuts_dispos[$this->session->userdata('statut')] == 'admin'){					echo '<td class="action"><a href="'.site_url('/annuaire/modifier/'.$contact['contact_id']).'" >'.$this->lang->line('general_modifier').'</a></td>';					if($contact['actif'] == 1)						echo '<td class="action"><a href="'.site_url('/annuaire/desactiver/'.$contact['contact_id']).'" >'.$this->lang->line('general_desactiver').'</a></td>';					else						echo '<td class="action"><a href="'.site_url('/annuaire/activer/'.$contact['contact_id']).'" >'.$this->lang->line('general_activer').'</a></td>';					$msg = sprintf($this->lang->line('annuaire_confirm_suppression'), $contact['prenom'].' '.$contact['nom']);					echo '<td class="action">';						echo '<a href="#" ';						echo 'onclick="if(confirm(\''.addslashes($msg).'\')) window.location.href=\''.site_url('/annuaire/supprimer/'.$contact['contact_id']).'\'" ';						echo '>'.$this->lang->line('general_supprimer').'</a>';					echo '</td>';				}				else{					echo '<td class="action" colspan="3"><a href="'.site_url('/annuaire/visualiser/'.$contact['contact_id']).'" >'.$this->lang->line('general_voir').'</a></td>';				}							echo '</tr>';		}	echo '</table>';}echo $pagination;?>