<?php$data = array(    'login' => array(        'name'        => 'login',        'maxlength'   => '255',        'size'        => '40',        'required'    => 'required',        'autofocus'   => 'autofocus',        'value'       => set_value('login'),    ),    'password' => array(        'name'        => 'password',        'maxlength'   => '255',        'size'        => '40',        'required'    => 'required',        'value'       => set_value('password'),    ),);echo '<div id="conteneur_connexion" >';	echo '<h2>'.$this->lang->line('global_titre_connexion').'</h2>';	$liste_erreurs = validation_errors();	if(!empty($liste_erreurs)){		echo '<div class="erreurs_form">';			echo '<p>'.$this->lang->line('global_erreur_form').'</p>';			echo '<div>';				echo $liste_erreurs;			echo '</div>';		echo '</div>';	}	echo form_open('connexion', array('id' => 'form_connexion', 'name' => 'form_connexion'));	$this->table->add_row(		form_label($this->lang->line('global_libelle_login').' <span class="requis">*</span>', 'login'),		form_input($data['login'])	);	$this->table->add_row(		form_label($this->lang->line('global_libelle_password').' <span class="requis">*</span>', 'password'),		form_password($data['password'])	);	$this->table->add_row(		'',		form_submit('submit_connexion', $this->lang->line('general_ok'))	);	echo $this->table->generate();	echo '</form>';echo '</div>';