<?php$data = array(    'login' => array(        'name'        => 'login',        'maxlength'   => '255',        'required'    => 'required',        'autofocus'   => 'autofocus',        'value'       => set_value('login'),		'class'		  => 'input-medium',		'placeholder' => $this->lang->line('global_libelle_login'),    ),    'password' => array(        'name'        => 'password',        'maxlength'   => '255',        'required'    => 'required',        'value'       => set_value('password'),		'class'		  => 'input-medium',		'placeholder' => $this->lang->line('global_libelle_password'),    ),);// $liste_erreurs = validation_errors();// if(!empty($liste_erreurs)){	// echo '<div class="erreurs_form">';		// echo '<p>'.$this->lang->line('global_erreur_form').'</p>';		// echo '<div>';			// echo $liste_erreurs;		// echo '</div>';	// echo '</div>';// }echo form_open('connexion', array('id' => 'form_connexion', 'name' => 'form_connexion', 'class' => 'well form-inline'));	if(form_error('login') || form_error('password')){		echo '<div class="alert alert-error">';			echo form_error('login');			echo form_error('password');		echo '</div>';	}	echo form_input($data['login']);	echo form_password($data['password']);	echo form_submit(array('name' => 'submit_connexion', 'class' => 'btn btn-primary'), $this->lang->line('general_ok'));echo '</form>';