<?php$action = empty($fonction['fonction_id']) ? 'fonctions/ajouter' : 'fonctions/modifier/'.$fonction['fonction_id'];$data = array(    'actif_oui' => array(        'name'        => 'actif',        'id'          => 'actif_oui',        'value'       => 1,		'checked'     => ($fonction['actif']==1),    ),	'actif_non' => array(        'name'        => 'actif',        'id'          => 'actif_non',        'value'       => 0,		'checked'     => ($fonction['actif']!=1),    ),    'nom' => array(        'name'        => 'nom',        'id'          => 'nom',        'maxlength'   => '255',        'size'        => '40',        'required'    => 'required',        'value'       => $fonction['nom'],    ),);$liste_erreurs = validation_errors();if(!empty($liste_erreurs)){	echo '<div class="erreurs_form">';		echo '<p>'.$this->lang->line('global_erreur_form').'</p>';		echo '<div>';			echo $liste_erreurs;		echo '</div>';	echo '</div>';}echo form_open($action, array('id' => 'form_fonction', 'name' => 'form_fonction'));	$this->table->add_row(		form_label($this->lang->line('fonctions_libelle_actif').' <span class="requis">*</span>'),		form_label(form_radio($data['actif_oui']).' '.$this->lang->line('general_oui'), $data['actif_oui']['id'], array('class' => 'label_radio'))		.form_label(form_radio($data['actif_non']).' '.$this->lang->line('general_non'), $data['actif_non']['id'], array('class' => 'label_radio'))	);	$this->table->add_row(		form_label($this->lang->line('fonctions_libelle_nom').' <span class="requis">*</span>', $data['nom']['id']),		form_input($data['nom'])	);	$this->table->add_row(		'',		form_submit('submit_fonction', $this->lang->line('general_valider'))		.'<button type="button" onclick="window.location.href=\''.site_url('/fonctions/liste').'\'" >'.$this->lang->line('general_retour').'</button>'	);	echo $this->table->generate();echo '</form>';echo '<p>'.sprintf($this->lang->line('general_champs_requis'), '<span class="requis">*</span>').'</p>';