<?php$action = empty($fonction['fonction_id']) ? 'fonctions/ajouter' : 'fonctions/modifier/'.$fonction['fonction_id'];$data = array(    'actif' => array(		'name'		  => 'actif',		'value'       => set_value('actif', $fonction['actif']),    ),    'nom' => array(        'name'        => 'nom',        'id'          => 'nom',        'maxlength'   => '255',        'size'        => '40',        'required'    => 'required',        'value'       => $fonction['nom'],		'placeholder' => $this->lang->line('fonctions_libelle_nom'),    ),);echo form_open($action, array('id' => 'form_fonction', 'name' => 'form_fonction', 'class' => 'well'));	echo '<div class="control-group">';		echo '<div class="controls">';			echo '<div class="btn-group" data-toggle="buttons-radio">';				$ext = ($data['actif']['value'] == 1) ? ' active' : '';				echo '<button class="btn'.$ext.'" type="button" onclick="toggle_radio(\'actif\', 1)" >'.$this->lang->line('fonctions_libelle_active').'</button>';				$ext = ($data['actif']['value'] != 1) ? ' active' : '';				echo '<button class="btn'.$ext.'" type="button" onclick="toggle_radio(\'actif\', 0)" >'.$this->lang->line('fonctions_libelle_inactive').'</button>';			echo '</div>';			echo form_hidden($data['actif']['name'], $data['actif']['value']);		echo '</div>';	echo '</div>';	if(form_error('nom'))		echo '<div class="control-group error">';	else		echo '<div class="control-group">';		echo '<div class="controls">';			echo form_input($data['nom']);			echo form_error('nom');		echo '</div>';	echo '</div>';	echo '<div class="form-actions">';		echo form_submit(array('name' => 'submit_fonction', 'class' => 'btn btn-primary'), $this->lang->line('general_valider'));		echo ' <button type="button" class="btn" onclick="window.location.href=\''.site_url('/fonctions/liste').'\'" >'.$this->lang->line('general_retour').'</button>';	echo '</div>';echo '</form>';