<?php$sess_fonction = $this->session->userdata('fonction');$tri = $sess_fonction['tri']['champ'] == 'nom' ? ($sess_fonction['tri']['type'] == 'asc' ? '<i class="icon-chevron-down"></i>' : '<i class="icon-chevron-up"></i>') : '';if(!empty($message)){	echo '<p class="alert alert-success">'.$this->lang->line($message).'</p>';}echo '<button type="button" class="btn btn-primary btn-large action_globale" onclick="window.location.href=\''.site_url('/fonctions/ajouter').'\'">'.$this->lang->line('general_ajouter').'</button>';if(!empty($tab_fonctions)){	echo '<section id="liste_fonctions">';		echo '<table  class="table table-bordered">';			echo '<thead>';				echo '<th class="triable" colspan="3">'.$tri.' <a href="'.site_url('/fonctions/tri/nom').'" title="'.$this->lang->line('general_trier').'" >'.$this->lang->line('fonctions_libelle_fonctions').'</a></th>';			echo '</thead>';			foreach($tab_fonctions as $fonction){				echo '<tr>';					if($fonction['actif'] == 1)						echo '<td class="active">'.$fonction['nom'].'</td>';					else						echo '<td>'.$fonction['nom'].'</td>';					echo '<td class="action"><a href="'.site_url('/fonctions/modifier/'.$fonction['fonction_id']).'" title="'.$this->lang->line('general_modifier').'"><i class="icon-pencil"></i></a></td>';					if($fonction['actif'] == 1)						echo '<td class="action"><a href="'.site_url('/fonctions/desactiver/'.$fonction['fonction_id']).'" title="'.$this->lang->line('general_desactiver').'"><i class="icon-minus"></i></a></td>';					else						echo '<td class="action"><a href="'.site_url('/fonctions/activer/'.$fonction['fonction_id']).'" title="'.$this->lang->line('general_activer').'"><i class="icon-ok"></i></a></td>';				echo '</tr>';			}		echo '</table>';	echo '</section>';}