<?php$sess_fonction = $this->session->userdata('fonction');if(!empty($message)){	echo '<p class="message">'.$this->lang->line($message).'</p>';}echo '<button type="button" onclick="window.location.href=\''.site_url('/fonctions/ajouter').'\'">'.$this->lang->line('general_ajouter').'</button>';echo '<br /><br />';if(!empty($tab_fonctions)){	echo '<table class="liste">';		echo '<thead>';			echo '<th><a href="'.site_url('/fonctions/tri/nom').'">'.$this->lang->line('fonctions_libelle_fonctions')			.($sess_fonction['tri']['champ'] == 'nom' ? ' <span>'.$sess_fonction['tri']['type'].'</span>' : '').'</a></th>';			echo '<th></th>';			echo '<th></th>';		echo '</thead>';		foreach($tab_fonctions as $fonction){			echo '<tr>';				echo '<td>'.$fonction['nom'].'</td>';				echo '<td class="action"><a href="'.site_url('/fonctions/modifier/'.$fonction['fonction_id']).'" >'.$this->lang->line('general_modifier').'</a></td>';				if($fonction['actif'] == 1)					echo '<td class="action"><a href="'.site_url('/fonctions/desactiver/'.$fonction['fonction_id']).'" >'.$this->lang->line('general_desactiver').'</a></td>';				else					echo '<td class="action"><a href="'.site_url('/fonctions/activer/'.$fonction['fonction_id']).'" >'.$this->lang->line('general_activer').'</a></td>';			echo '</tr>';		}	echo '</table>';}