<?php$sess_user = $this->session->userdata('user');if(!empty($message)){	echo '<p class="message">'.$this->lang->line($message).'</p>';}echo '<button type="button" onclick="window.location.href=\''.site_url('/users/ajouter').'\'">'.$this->lang->line('general_ajouter').'</button>';echo '<br /><br />';if(!empty($tab_users)){	echo '<table class="liste">';		echo '<thead>';			echo '<th><a href="'.site_url('/users/tri/membre').'">'.$this->lang->line('users_libelle_users')			.($sess_user['tri']['champ'] == 'membre' ? ' <span>'.$sess_user['tri']['type'].'</span>' : '').'</a></th>';			echo '<th></th>';			echo '<th></th>';			echo '<th></th>';		echo '</thead>';		foreach($tab_users as $user){			echo '<tr>';				$membre = empty($user['prenom']) ? $user['nom'] : $user['prenom'].' '.$user['nom'];				echo '<td>'.$membre.'</td>';				echo '<td class="action"><a href="'.site_url('/users/modifier/'.$user['user_id']).'" >'.$this->lang->line('general_modifier').'</a></td>';				if($this->session->userdata['user_id'] != $user['user_id']){					if($user['actif'] == 1)						echo '<td class="action"><a href="'.site_url('/users/desactiver/'.$user['user_id']).'" >'.$this->lang->line('general_desactiver').'</a></td>';					else						echo '<td class="action"><a href="'.site_url('/users/activer/'.$user['user_id']).'" >'.$this->lang->line('general_activer').'</a></td>';					$msg = sprintf($this->lang->line('users_confirm_suppression'), $membre);					echo '<td class="action">';						echo '<a href="#" ';						echo 'onclick="if(confirm(\''.addslashes($msg).'\')) window.location.href=\''.site_url('/users/supprimer/'.$user['user_id']).'\'" ';						echo '>'.$this->lang->line('general_supprimer').'</a>';					echo '</td>';				}				else					echo '<td class="action"></td><td class="action"></td>';			echo '</tr>';		}	echo '</table>';}?>